import React, {Fragment, useEffect}from 'react';
import MetaData from '../layouts/MetaData';
import {useDispatch, useSelector} from 'react-redux';
import { myOrders, clearErrors } from '../../actions/ordersActions';
import 'react-toastify/dist/ReactToastify.css';
import { addItemToCart } from '../../actions/cartActions';
const ListOrders = () => {

    const dispatch = useDispatch();

    const { loading, error, orders } = useSelector(state => state.myOrders);
    useEffect(() => {
        dispatch(myOrders());
        if(error){
            toast.error(error);
            dispatch(clearErrors());
        }
    }, [dispatch, error]);

    const setOrders = () => {
        const data = {
            columns: [
                {
                    label: 'Nº Pedido',
                    field: 'id',
                    sort: 'asc'
                },
                {
                    label: 'Data',
                    field: 'createdAt',
                    sort: 'asc'
                },
                {
                    label: 'Total',
                    field: 'totalPrice',
                    sort: 'asc'
                },
                {
                    label: 'Status',
                    field: 'orderStatus',
                    sort: 'asc'
                },
                {
                    label: 'Detalhes',
                    field: 'actions',
                    sort: 'asc'
                }
            ],
            rows: []
        }
        orders.forEach(order => {
            data.rows.push({
                id: order._id,
                numOfItems: order.orderItems.length,
                amount: `$${order.totalPrice}`,
                createdAt: String(order.createdAt).substring(0, 10),
                status: order.orderStatus && String(order.orderStatus).includes('Delivered') ? (
                    <p className="text-success">{order.orderStatus}</p>
                ) : (
                    <p className="text-danger">{order.orderStatus}</p>
                ),
                actions: <a href={`/order/${order._id}`} className="btn btn-primary"><i className="fa fa-eye"></i></a>
            })
        });
        return data;
    }

    return (
        <Fragment>
            <MetaData title={'Meus Pedidos'} />
            <h1 className="my-5">Meus Pedidos</h1>

            <table className="table table-bordered table-responsive-sm">
                <thead className="bg-primary">
                    <tr>
                        <th>Nº Pedido</th>
                        <th>Data</th>
                        <th>Total</th>
                        <th>Status</th>
                        <th></th>
                    </tr>
                </thead>

                <tbody>
                    {orders && orders.map(order => (
                        <tr key={order._id}>
                            <td>{order._id}</td>
                            <td>{String(order.createdAt).substring(0, 10)}</td>
                            <td>R$ {order.totalPrice}</td>
                            <td className="text-center">
                                {order.orderStatus && String(order.orderStatus).includes('Delivered') ? (
                                    <p className="text-success">{order.orderStatus}</p>
                                ) : (
                                    <p className="text-danger">{order.orderStatus}</p>
                                )}
                            </td>
                            <td className="text-center">
                                <button className="btn btn-primary btn-sm" onClick={() => dispatch(addItemToCart(order.cart))}>Detalhes</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            </Fragment>
    );
    }
    export default ListOrders;
