import React, { Fragment, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import MetaData from '../layouts/MetaData';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import axios from 'axios';

const Payment = ({ history }) => {
  const [cardInfo, setCardInfo] = useState({
    cardNumber: '',
    expiryDate: '',
    cvc: '',
  });

  const { user } = useSelector((state) => state.auth);
  const { cartItems, shippingInfo } = useSelector((state) => state.cart);
  const dispatch = useDispatch();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Simulação de requisição de pagamento (pode ser substituída por chamada real à API)
    setTimeout(() => {
      toast.success('Pagamento aprovado');
      history.push('/'); // Redirecionar após o pagamento
    }, 2000); // Simulando um tempo de espera para a resposta do pagamento
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCardInfo({ ...cardInfo, [name]: value });
  };

  return (
    <Fragment>
      <MetaData title={'Pagamento'} />
      <div className="row wrapper">
        <div className="col-10 col-lg-5">
          <form className="shadow-lg" onSubmit={handleSubmit}>
            <h1 className="mb-4">Card Info</h1>
            <div className="form-group">
              <label htmlFor="card_num_field">Numero do Cartão</label>
              <input
                type="text"
                id="card_num_field"
                className="form-control"
                name="cardNumber"
                value={cardInfo.cardNumber}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="card_exp_field">Data de vencimento do Cartão</label>
              <input
                type="text"
                id="card_exp_field"
                className="form-control"
                name="expiryDate"
                value={cardInfo.expiryDate}
                onChange={handleChange}
              />
            </div>

            <div className="form-group">
              <label htmlFor="card_cvc_field">CVC do Cartão</label>
              <input
                type="text"
                id="card_cvc_field"
                className="form-control"
                name="cvc"
                value={cardInfo.cvc}
                onChange={handleChange}
              />
            </div>

            <button type="submit" className="btn btn-block py-3">
              Pay
            </button>
          </form>
        </div>
      </div>
    </Fragment>
  );
};

export default Payment;
