import React, { Fragment, useEffect } from 'react'
import MetaData from './layouts/MetaData'

import { useDispatch, useSelector } from 'react-redux'
import { getProducts } from '../actions/productActions'

const Home = () => {

    const dispatch = useDispatch();

    const { loading, products, error, productsCount } = useSelector(state => state.products)

    useEffect(() => {
        dispatch(getProducts())
    }, [dispatch])

    return (
        <Fragment>
            <MetaData title={'Compre os melhores produtos eletrônicos'} />

            <h1 id='products_heading'>Ultimos Produtos</h1>

            <section id='products' className='container mt-5'>
                <div className='row'>
                    <div className='row'>
                        {products && products.map(product => {
                            if (product.images && product.images.length > 0) {
                                return (
                                    <div key={product._id} className='col-sm-12 col-md-6 col-lg-3 my-3'>
                                        <div className='card p-3 rounded'>
                                            <img
                                                className='card-img-top mx-auto'
                                                src={product.images[0].url} />
                                            {/* O restante do código da card */}
                                        </div>
                                    </div>
                                )
                            } else {
                                console.error('O produto', product._id, 'não possui imagens.');
                                return null;
                            }
                        })}
                    </div>
                </div>
            </section>
        </Fragment>

    )
}

export default Home
